document.addEventListener("DOMContentLoaded",()=>{fetch("json/data.json").then(e=>e.json()).then(e=>{document.getElementById("text2").textContent=e.header.text2,document.getElementById("text3").textContent=e.header.text3,document.getElementById("text4").textContent=e.header.text4;let n=document.getElementById("skills");e.skills.forEach(e=>{var t=document.createElement("div");t.classList.add("skill"),t.innerHTML=`
                    <span>${e.name}</span>
                    <input type="range" value="${e.value}" max="100">
                `,n.appendChild(t)});e=document.getElementById("eduToggle");let t=document.getElementById("eduContent"),d=e.querySelector(".arrow");e.addEventListener("click",()=>{t.classList.toggle("open"),d.classList.toggle("open")})}).catch(e=>console.error("JSON load error:",e))});